import React from 'react';
import './Hero.css';
import yarifeImage from '../../assets/images/Fotos profesionales/1.png';

const Hero = () => {
  const scrollToContact = () => {
    const element = document.getElementById('contacto');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="inicio" className="hero">
      {/* Lado izquierdo - Morado */}
      <div className="hero-left">
        {/* Patrón de ondas */}
        <div className="wave-pattern"></div>
        
        {/* Iconos */}
        <i className="fas fa-lightbulb hero-icon lightbulb-icon"></i>
        <i className="fas fa-arrow-down hero-icon arrow-icon"></i>
        
        {/* Contenido */}
        <div className="hero-content">
          <h1 className="hero-headline">
            DESATA EL<br />
            <span className="power">POWER</span><br />
            DETRÁS<br />
            DE TUS IDEAS
          </h1>
          
          <p className="hero-description">
            Transforma tu conocimiento en una marca profesional que educa, conecta y vende.
          </p>
          
          <button onClick={scrollToContact} className="cta-button">
            ASESORÍA GRATUITA
          </button>
        </div>
      </div>
      
      {/* Lado derecho - Naranja */}
      <div className="hero-right">
        {/* Círculo de fondo */}
        <div className="circle-bg"></div>
        
        {/* Imagen de la persona */}
        <div className="person-image">
          <img src={yarifeImage} alt="Yarife Vallenilla" />
        </div>
      </div>
    </section>
  );
};

export default Hero;
