import React from 'react';
import './ProductsShowcase.css';

const products = [
  {
    id: 'curso-canva',
    tag: 'CURSO',
    title: 'Crea una presentación con Canva desde cero',
    cta: 'Ver curso',
    url: 'https://guaybo.com/yarifepower/crea-una-presentacion-con-canva-desde-cero-1',
  },
  {
    id: 'ebook-presentaciones',
    tag: 'EBOOK',
    title: 'Aprende a diseñar presentaciones efectivas y atractivas',
    cta: 'Ver ebook',
    url: 'https://guaybo.com/yarifepower/ebook-aprende-a-disenar-presentaciones-efectivas-y-atractivas',
  },
];

const ProductsShowcase = () => {
  return (
    <section className="products-section">
      <div className="products-container">
        <h2 className="products-title">Aprende con Yarife</h2>
        <div className="products-grid">
          {products.map((p) => (
            <div key={p.id} className="product-card">
              <div className="product-top">
                <span className={`product-tag ${p.tag === 'CURSO' ? 'is-course' : 'is-ebook'}`}>{p.tag}</span>
              </div>
              <h3 className="product-name">{p.title}</h3>
              <div className="product-actions">
                <a href={p.url} target="_blank" rel="noreferrer" className="product-cta">
                  {p.cta}
                </a>
              </div>
            </div>
          ))}
        </div>
        <div className="products-footnote">
          <a className="secondary-link" href="https://guaybo.com/yarifepower" target="_blank" rel="noreferrer">Explora más en Guaybo</a>
        </div>
      </div>
    </section>
  );
};

export default ProductsShowcase;
